import React from 'react'; 
import Header from '../components/header';
import AdminList from '../components/adminList';
import { Link, withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import { actions, store } from "../store/store";
import axios from "axios"


class ProdukAdmin extends React.Component {

    state = {
        produkList : []
    }

    componentDidMount = async () => {
        let transaksi = {
            method:"get",
            url: "http://0.0.0.0:5000/produk",
            headers: {
                "Content-Type": "application/json"
            }
        };

        await axios(transaksi)
            .then((response) => {
                this.setState({produkList : response.data})
            })
            .catch(function(error) {
                console.log(error)
            })
    }

    render () {

        const produkData = this.state.produkList
        const produkDetail = produkData.map((item) => {
            return (
                <tr className='bg-white'>
                    <td>{item.id}</td>
                    <td><Link style={{color:'black'}}>{item.nama_produk}</Link></td>
                    <td>{item.kategori}</td>
                    <td>{item.harga}</td>
                    <td>{item.stok}</td>
                    <td>{item.lokasi}</td>
                </tr>
            );
        });

        return (
            <React.Fragment>
                <Header/>
                <div className="container-fluid">
                    <div className="row ml-3 mr-3">
                        <div className="col-md-3 col-lg-3 col-sm-12 col-12 mt-5">
                           <AdminList/>
                        </div>
                        <div className="col-md-9 col-lg-9 col-sm-12 col-12 mt-5 pl-5">
                            <table class="table table-sm">
                                <thead>
                                    <tr className='bg-warning'>
                                    <th scope="col">ID</th>
                                    <th scope="col">Nama Penerima</th>
                                    <th scope="col">Alamat</th>
                                    <th scope="col">No HP Penerima</th>
                                    <th scope="col">Total Harga</th>
                                    <th scope="col">Status</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {produkDetail}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>

            </React.Fragment>

        )
    }
}
export default connect('profilData', actions)(withRouter(ProdukAdmin));
