import React from 'react'; 
import Header from './header';
import { withRouter, Link } from "react-router-dom";
import { connect } from "unistore/react";
import { actions, store } from "../store/store";
import logo from '../images/logo.svg'
import axios from "axios"

class RiwayatPesanan extends React.Component {

    componentDidMount = async () => {
        const self = this
        let transaksi = {
            method:"get",
            url: "http://0.0.0.0:5000/transaksi",
            headers: {
                "Content-Type": "application/json",
                'Authorization':'Bearer ' + localStorage.getItem("token")
            }
        };

        await axios(transaksi)
            .then((response) => {
                console.warn("response data transaksi", response.data);

                store.setState({transaksiList : response.data})
            })
            .catch(function(error) {
                console.log(error)
            })
    }


    render () {

        let transaksi;
        let transaksiDetail;
        if(this.props.transaksiList!==undefined) {
            transaksi = this.props.transaksiList

            transaksiDetail = transaksi.map((value) => {
                return (
                    <div className="row border bg-white py-5 pl-3 pr-3">
                        <h2>Order ID : {value.id}</h2>
                        <h3>Nama Penerima : {value.nama_penerima} </h3>
                        <h3>No HP : {value.no_hp_penerima}</h3>
                        <h3>Alamat : {value.alamat}</h3>
                        <h3>Total Harga : {value.total_harga}</h3>
                        <h3>Status : {value.status}</h3>
                    </div>
                )
            })
        }

        return (
            <React.Fragment>
                <div className="container">
                    {transaksiDetail}
                </div>
            </React.Fragment>
        )
    }
}
export default  connect('transaksiList', actions)(withRouter(RiwayatPesanan));
