import React from 'react'; 
import { withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import { actions } from "../store/store";
class RiwayatPesanan extends React.Component {

    render () {

        return (
            <React.Fragment>
                <div className="container">
                    <div className="border bg-white py-3 pl-3 pr-3">
                        <h4>Order ID : {props.value.id}</h4>
                        <h6>Nama Penerima : {props.value.nama_penerima} </h6>
                        <h6>No HP : {props.value.no_hp_penerima}</h6>
                        <h6>Alamat : {props.value.alamat_pengiriman}</h6>
                        <h6>Total Harga : {props.value.total_harga}</h6>
                        <h6>Status : {props.value.status}</h6>
                    </div>
                </div>
            </React.Fragment>
        )
    }
}
export default  connect('', actions)(withRouter(RiwayatPesanan));
