import React from 'react'; 
import Header from '../components/header';
import { withRouter, Link } from "react-router-dom";
import { connect } from "unistore/react";
import { actions, store } from "../store/store";
import axios from "axios"
import deleted from '../images/delete.svg'
import ProfilList from '../components/profilList';

class Wishlist extends React.Component {

    componentDidMount = async () => {
        this.getWishlist()
    }

    getWishlist = async () => {
        let wishlist = {
            method:"get",
            url: "http://0.0.0.0:5000/wishlist",
            headers: {
                "Content-Type": "application/json",
                'Authorization':'Bearer ' + localStorage.getItem("token")
            }
        };

        await axios(wishlist)
            .then((response) => {
                store.setState({wishlistData : response.data})
            })
            .catch(function(error) {
                console.log(error)
            })
    }

    hapusProduk = async wishlistId => {
        const self = this
        let wishlist = {
            method:"delete",
            url: "http://0.0.0.0:5000/wishlist/"+(wishlistId)*1,
            headers: {
                "Content-Type": "application/json",
                'Authorization':'Bearer ' + localStorage.getItem("token")
            }
        };

        await axios(wishlist)
            .then((response) => {
                alert('produk berhasil di hapus dari wishlist')
            })
            .catch(function(error) {
                console.log(error)
            })

        self.getWishlist()
    }


    render () {

        let wishlistDetail;
        let keranjangProduk;
        if(this.props.wishlistData!==undefined) {
            wishlistDetail = this.props.wishlistData

            keranjangProduk = wishlistDetail.map((value) => {
                return (
                    <tbody>
                        <tr>
                            <td className="product-col">
                                <img src={value.foto_produk} alt=""/>
                                <div className="pc-title">
                                    <h4>{value.produk.nama_produk}</h4>
                                    <Link onClick={()=>this.hapusProduk(value.id)}><img src={deleted} alt="" width='20px'/></Link>
                                </div>
                            </td>
                            <td className="price-col">Rp. {value.produk.harga}</td>
                        </tr>
                    </tbody>
                )
            })
        }

        return (
            <React.Fragment>
                <Header/>
                <div className="page-area cart-page spad mt-5">
                    <div className="container-fluid">
                        <div className="row mt-5 ml-3 mr-3">
                            <div className="col-md-3 col-lg-3 col-sm-12 col-12">
                                <ProfilList />
                            </div>
                            <div className="col-md-1"></div>
                            <div className="col-md-7 col-lg-7 col-sm-12 col-12">
                                    <div className="cart-table">
                                        <table>
                                            <thead>
                                                <tr style={{backgroundColor:'#fcc200'}}>
                                                    <th className="product-th text-center">Produk</th>
                                                    <th>Harga</th>
                                                </tr>
                                            </thead>
                                            {keranjangProduk}
                                        </table>
                                    </div>
                                </div>
                            </div>
                            <div className="col-lg-8 pl-5">
                                <div className="cart-total-details">
                                </div>
                            </div>
                            </div>
                        </div>
            </React.Fragment>

        )
    }
}
export default  connect('auth, keranjang, keranjangData, kuantitas', actions)(withRouter(Wishlist));
